mapper.insert:

  			SELECT	'com'||lpad(com_seq.nextval, 5, 0) from dual

		INSERT into COMMENTS(
		    COMT_ID
		    , COMT_CNT
		    , comt_date
		    , comt_secret
		    , comt_del
		    , comt_rel_art_id
		    , mem_id
		    , FILTER_ID
		)values(
		     #{comt_id}
		    , #{comt_cnt}
		    , sysdate
		    , 'N'
		    , 'N'
		    , #{comt_rel_art_id}
		    , #{mem_id}
		    , #{filter_id}
		)

mapper.update:
UPDATE COMMENTS
set comt_del = 'Y'
where comt_id = #{comt_id}

UPDATE COMMENTS
set comt_cnt = #{comt_cnt}
where comt_id = #{comt_id}
