mapper.select:
SELECT chatroom_name
FROM participant
WHERE group_id = #{group_id}
AND mem_id = #{mem_id}

SELECT mem_id
FROM participant
WHERE group_id = #{group_id}

mapper.delete:
DELETE FROM participant
WHERE group_id = #{group_id}
AND	mem_id = #{mem_id}

mapper.update:
UPDATE participant
SET chatroom_name = #{chatroom_name}
WHERE group_id = #{group_id}
AND mem_id = #{mem_id}

mapper.select:
SELECT COUNT(*)
FROM participant
WHERE group_id = #{group_id}

mapper.update:
UPDATE chatroom
SET group_del = 'Y'
WHERE group_id = #{group_id}
