mapper.update:
update tripplan 
set tripplan_title = #{tripplan_title},tripplan_theme = #{tripplan_theme}, tripplan_season = #{tripplan_season} ,tripplan_peo_type = #{tripplan_peo_type},tripplan_peo_count = #{tripplan_peo_count},tripplan_image = #{tripplan_image}
where tripplan_id = #{tripplan_id}

mapper.select:
select mm.mapmark_sido mapmark_sido, mm.mapmark_sigungu mapmark_sigungu, mm.mapmarker_enbmyeondong mapmarker_enbmyeondong, mm.mapmark_x_coor mapmark_x_coor, mm.mapmark_y_coor mapmark_y_coor, tp.tripplan_id tripplan_id, tp.tripplan_title tripplan_title, tp.tripplan_theme tripplan_theme, tp.tripplan_season tripplan_season, tp.tripplan_peo_type tripplan_peo_type, tp.tripplan_start_day tripplan_start_day, tp.tripplan_days tripplan_days, tp.tripplan_peo_count tripplan_peo_count, tp.mem_id mem_id, tp.tripplan_image tripplan_image, tp.tripplan_del tripplan_del, m.mem_nick mem_nick, e.essay_id essay_id, e.essay_meal_exp essay_meal_exp, e.essay_traffic_exp essay_traffic_exp, e.essay_room_exp essay_room_exp, e.essay_other_exp essay_other_exp from mapmarker mm, triparea ta, tripplan tp, member m, essay e where mm.triparea_id = ta.triparea_id and ta.triparea_order = 1 and ta.tripplan_id = tp.tripplan_id and tp.tripplan_del = 'N' and m.mem_id = tp.mem_id and e.essay_del = 'N' and e.tripplan_id = tp.tripplan_id
and tp.tripplan_theme = #{tripplan_theme}
and tp.tripplan_season = #{tripplan_season}
and tp.tripplan_peo_type = #{tripplan_peo_type}
and mm.mapmark_sido like #{sido}||'%'
and mm.mapmark_sigungu like #{sigungu}||'%'
and mm.mapmarker_enbmyeondong like #{enmyeondong}||'%'
